<?phpimport('core.datasource.DataAccess');import('core.datasource.TStaticQuery');function getUserProfile($id){		$sql = "select * from cms_profile where user_id = {$id}";		$result = TStaticQuery::queryList($GLOBALS['currentApp']['dbaccess'],$sql);		return $result[0];	}function avatarSave($headImageUrl,$staffId){echo $headImageUrl;echo $staffId;	$sql = "update {$GLOBALS['table']['member']['staff']} set headImageUrl = '{$headImageUrl}' where staffId = '{$staffId}'";	TStaticQuery::updatedata($GLOBALS['currentApp']['dbaccess'],$sql);	}function screen_shot($name,$src,$x1,$x2,$y1,$y2,$w,$h,$cname,$resize=null){ $type = strtolower(substr(strrchr($name,"."),1)); $aExtraInfo = getimagesize($src);  if($type == 'jpeg') $type = 'jpg';  switch($type){    case 'bmp': $img = imagecreatefromwbmp($src); break;    case 'gif': $img = imagecreatefromgif($src); break;    case 'jpg': $img = imagecreatefromjpeg($src); break;    case 'png': $img = imagecreatefrompng($src); break;    default : return "Unsupported picture type!";  }    if($resize!=null){	   $o_width = $aExtraInfo["0"];	   $o_height = $aExtraInfo["1"];    }else{    	$o_width = $w;    	$o_height = $h;    }      $new = imagecreatetruecolor($w, $h); if($type == 'jpeg') $type = 'jpg';  switch($type){    case 'bmp': $img = imagecreatefromwbmp($src);    imagecopyresampled($new, $img, 0, 0, $x1, $y1, $w, $h, $o_width, $o_height);    imagewbmp($new, $cname.".".$type);     imagedestroy($new);    imagedestroy($img);    break;    case 'gif': $img = imagecreatefromgif($src);    imagecopyresampled($new, $img, 0, 0, $x1, $y1, $w, $h, $o_width, $o_height);    imagegif($new, $cname.".".$type);    imagedestroy($new);    imagedestroy($img);    break;    case 'jpg': $img = imagecreatefromjpeg($src);    imagecopyresampled($new, $img, 0, 0, $x1, $y1, $w, $h, $o_width, $o_height);    imagejpeg($new, $cname.".".$type,100);    imagedestroy($new);    imagedestroy($img);     break;    case 'png': $img = imagecreatefrompng($src);    imagecopyresampled($new, $img, 0, 0, $x1, $y1, $w, $h, $o_width, $o_height);    imagepng($new, $cname.".".$type);     imagedestroy($new);    imagedestroy($img);    break;    default : return "Unsupported picture type!";  }    return $type;	}?>